  /**
   * Finds an investorindividual for the given application of the given id.
   * 
   * @param application The application.
   * @param investorIndividualId The investorindividual's id.
   * 
   * @return The investor individual.
   */
  public Investorindividual findInvestorIndividualById(Long investorIndividualId) {
    Investorindividual investorindividual = null;
    
    for (Investorindividual tmp : this.getInvestorindividuals()) {
      if (tmp.getId().equals(investorIndividualId)) {
        investorindividual = tmp;
        break;
      }
    }
    
    return investorindividual;
  }
  
  /**
   * Gets all investor individuals associated to this application that
   * are elegible to act as guarantors.
   * 
   * @param application The application to check.
   * 
   * @return Elegible guarantors associated to the given application.
   */
  public Set<Investorindividual> getElegibleAdditionalGuarantors() {
    Set<Investorindividual> elegibleGuarantors = new HashSet<Investorindividual>();
    
    for (Investorindividual investorindividual : this.getInvestorindividuals()) {
      // directors already had the option to be marked as guarantors.
      if (investorindividual.getRole().equals(Constants.DIRECTOR)) {
        continue;
      }
      
      // remove external guarantors.  they are seperate to this logic.
      if (investorindividual.getRole().equals(Constants.GUARANTOR)) {
        continue;
      }
      
      elegibleGuarantors.add(investorindividual);
    }
    
    return elegibleGuarantors;
  }
  
  /**
   * Gets the external guarantor for this application, if any.
   * 
   * @return The IndividualInvestor representing the external guarantor, or
   *         <code>null</code> if none found.
   */
  public Investorindividual getExternalGuarantor() {
    Investorindividual externalGuarantor = null;
    
    for (Investorindividual investorindividual : this.investorindividuals) {
      if (investorindividual.getRole().equals(Constants.GUARANTOR)) {
        externalGuarantor = investorindividual;
        break;
      }
    }
    
    return externalGuarantor;
  }
  
  /**
   * Checks for any non-external guarantors.  Excludes corporate directors.
   * 
   * @return Internal guarantors.
   */
  public Investorindividual getInternalGuarantor() {
    Investorindividual additionalGuarantor = null;
    
    for (Investorindividual investorindividual : this.investorindividuals) {
      if (!investorindividual.getRole().equals(Constants.GUARANTOR)
          && !investorindividual.getRole().equals(Constants.DIRECTOR)
          && investorindividual.getIsguarantor().equals(Constants.TRUE)) {
        additionalGuarantor = investorindividual;
        break;
      }
    }
    
    return additionalGuarantor;
  }

