  // added by paulmac
  public boolean isSoledirector() {
    if ((this.type == Constants.CORPORATE_TYPE) ||
        (this.type == Constants.CORPORATE_TRUST_TYPE)) {
      for (Investorindividual individual : investorindividuals) {
        if (individual.getIssoledirector().equals(Constants.TRUE))
          return true;
      }
    }

    return false;
  }

  public boolean isHasSufficientDirectors() {
    return ((this.type == CORPORATE_TYPE) ||
           (this.type == CORPORATE_TRUST_TYPE)) &&
           (this.isSoledirector() || (this.investorindividuals.size() == 2));
  }

