  private int getBusinessday(int dayOfWeek, int dayOfYear) {
    if (dayOfWeek == Calendar.SATURDAY)
      return dayOfYear + 2;
    else if (dayOfWeek == Calendar.SUNDAY)
      return dayOfYear + 1;
    else
      return dayOfYear;
  }

  public String getDisplaypaymentstatus() {
    if (this.getScheduleddddate() == null)
      return Constants.PAY_STATE_PENDING;
    else {
      boolean hasIssue =
        this.getOpsissueraisedticked().equals(Constants.TRUE) &&
        this.getOpsissueresolvedticked().equals(Constants.FALSE);

      boolean eftOk = false;

      if (this.getFirstddstatussucceeded().equals(Constants.TRUE) ||
          this.getSecondddstatussucceeded().equals(Constants.TRUE) ||
          this.getBpaystatussucceeded().equals(Constants.TRUE) ||
          this.getDirectcreditstatussucceeded().equals(Constants.TRUE))
        eftOk = true;

      Calendar calendar = new GregorianCalendar();
      calendar.setTime(new Date());

      int nowDayOfWeek = calendar.get(Calendar.DAY_OF_WEEK);
      int nowDayofYear = calendar.get(Calendar.DAY_OF_YEAR); // delay due to 'Link' retardadation
      Calendar sdddCalendar = new GregorianCalendar();
      sdddCalendar.setTime(this.getScheduleddddate());

      int sddreplydayofyear = sdddCalendar.get(Calendar.DAY_OF_YEAR) + 3; // add 3 day delay for reply to arrive
      int sdddayofweek = sdddCalendar.get(Calendar.DAY_OF_WEEK);
      int sddreplydayofweek =
        (sdddayofweek > 4) ? (sdddayofweek - 4) : (sdddayofweek + 3);

      if (this.getBusinessday(nowDayOfWeek, nowDayofYear) < this.getBusinessday(
        sddreplydayofweek,
        sddreplydayofyear))
        return Constants.PAY_STATE_SCHEDULED;
      else if (eftOk && !hasIssue)
        return Constants.PAY_STATE_PAID;
      else
        return Constants.PAY_STATE_QUERY;
    }
  }

