  public long getPaymentmethod() {
    if (this.isborrowingfromubs.equals(Constants.TRUE) &&
        this.isial.equals(Constants.FALSE)) // pay for units with UBS Loan
     {
      return 0;
    } else if (this.isborrowingfromubs.equals(Constants.TRUE) &&
               this.isial.equals(Constants.TRUE)) // pay for units with UBS Loan
     {
      return 1;
    } else if (this.isborrowingfromubs.equals(Constants.FALSE) &&
               this.ispurchasingputfromubs.equals(Constants.TRUE)) // pay for units with UBS Loan
     {
      return 2;
    } else if (this.isborrowingfromubs.equals(Constants.FALSE) &&
               this.ispurchasingputfromubs.equals(Constants.FALSE)) // pay for units with UBS Loan
     {
      return 3;
    } else
      return 0;
  }

  public String getDisplaypaymentmethod() {
    if (this.isborrowingfromubs.equals(Constants.TRUE) &&
        this.isial.equals(Constants.FALSE)) // pay for units with UBS Loan
     {
      return "UBS Loan";
    } else if (this.isborrowingfromubs.equals(Constants.TRUE) &&
               this.isial.equals(Constants.TRUE)) // pay for units with UBS Loan
     {
      return "UBS Interest Loan";
    } else if (this.isborrowingfromubs.equals(Constants.FALSE) &&
               this.ispurchasingputfromubs.equals(Constants.TRUE)) // pay for units with UBS Loan
     {
      return "Direct with Put";
    } else if (this.isborrowingfromubs.equals(Constants.FALSE) &&
               this.ispurchasingputfromubs.equals(Constants.FALSE)) // pay for units with UBS Loan
     {
      return "Direct without Put";
    } else
      return "Undefined!";
  }

  public void setPaymentmethod(long paymentmethod) {
    if (paymentmethod == 0) // pay for units with UBS Loan
     {
      this.isborrowingfromubs = Constants.TRUE;
      this.ispurchasingputfromubs = Constants.TRUE;
      this.isial = Constants.FALSE;
    } else if (paymentmethod == 1) // Buy Units with UBS Loan and Interest Assistance Loan
     {
      this.isborrowingfromubs = Constants.TRUE;
      this.ispurchasingputfromubs = Constants.TRUE;
      this.isial = Constants.TRUE;
    } else if (paymentmethod == 2) // Buy Units and Put Option with my own funds
     {
      this.isborrowingfromubs = Constants.FALSE;
      this.ispurchasingputfromubs = Constants.TRUE;
      this.isial = Constants.FALSE;
    } else // if (paymentmethod == 3) // Buy Units with my own funds (without Put Option)
     {
      this.isborrowingfromubs = Constants.FALSE;
      this.ispurchasingputfromubs = Constants.FALSE;
      this.isial = Constants.FALSE;
    }
  }

